package ch04;

public class IfExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score=93;
		
		if(score>90) {
			System.out.println("higher than 90");
			System.out.println('A');
		}
		if(score<90)
			System.out.println("lower than 90");
		System.out.println('B');
		
		
		
	}

}
